import React, { useRef, useEffect } from 'react';
import videoSource from '../static/media/HomePage.mov'; // Assuming correct format

const Video = () => {
  const videoRef = useRef(null);

  useEffect(() => {
    const video = videoRef.current;

    if (video) {
      // Address autoplay policies:
      if (typeof video.play === 'function') {
        // Browser supports play() without restrictions
        video.play().then(() => {
          video.loop = true; // Enable infinite loop
        }).catch(error => {
          console.error('Error playing video:', error);
        });
      } else {
        // Fallback for browsers that require a user gesture:
        video.addEventListener('click', () => {
          video.play().then(() => {
            video.loop = true;
          }).catch(error => {
            console.error('Error playing video:', error);
          });
        });
      }
    }
  }, []);

  return (
    <div class="h-auto pt-20" id="home"><video class="videoTag h-auto" autoplay="" loop="">
      <source src="/static/media/HomePage.69c796aa188ec15774de.mov" type="video/mp4"/></video>
      </div>
  );
};

export default Video;
