import React, { useState } from 'react';
import mainImage from '../static/media/RoadMap.png';
import callOut from '../static/media/Callout0002.png';

const Team = () => {
  const teamMembers = [
    {
      name: 'Arun HM',
      title: 'The Architect',
      description:
        'Head Business Analyst. Tactically and diplomatically solves problems and tames any storm in the way. Has a proven and celebrated background in requirement elicitation, process improvement and stakeholder relationship management for organizations that provide digital solutions.',
      imgSrc: '../static/media/Teams0001.png',
    },
    {
      name: 'Anil Kumar',
      title: 'Pencil King',
      description:
        'Chief artist and blueprint maker. Known to bring historical stories and characters to life with ease and grace. Always a busy body, neck deep, or should we say pencil deep, in freelance work for over two decades - work that includes character art, character design and illustrations.',
      imgSrc: '../static/media/Teams0002.png',
    },
    {
      name: 'Hrithik Kariyappa',
      title: 'Star Tech',
      description:
        'Discord Manager and Head of Problem Solving. Has a degree in computer science engineering, a diploma in 3D and VFX and over 5 years\' experience working in the IT industry.',
      imgSrc: '../static/media/Teams0003.png',
    },
    {
      name: 'Vaibhav',
      title: 'La Scholar',
      description:
        'Web3 developer. Computer science engineer. Known within the group as being brainier than most. When not developing softwares, backpacks and travels on his beloved bike to feed his spiritual intellect.',
      imgSrc: '../static/media/Teams0004.png',
    },
    {
      name: 'Rachita Wesley',
      title: 'The Octopus',
      description:
        'Not just an artist with a Masters degree in Fine Art. Has her fingers . . . oops, tentacles . . . in several pies. Over a decade\'s experience in designing characters, designing/developing games and graphics for games.',
      imgSrc: '../static/media/Teams0005.png',
    },
  ];

  const [selectedMember, setSelectedMember] = useState(teamMembers[0]);

  return (
    <div className="h-auto md:h-screen" id="team">
      <div className="h-full flex w-full mx-auto items-center justify-center md:flex-row flex-col">
        <div className="md:w-1/2 w-full text-center">
          <h1 className="text-5xl text-white font-extrabold pb-10">Team</h1>
          <div className="flex justify-evenly">
            {teamMembers.map((member, index) => (
              <div
                key={index}
                onMouseEnter={() => setSelectedMember(member)}
              >
                <img
                  src={member.imgSrc}
                  alt=""
                  className="md:h-48 h-28 hover:shadow-2xl rounded-3xl hover:scale-110 transition-all duration-500"
                />
                <h1 className="pt-2 text-xl text-white font-bold">
                  {member.name.split(' ')[0]}
                </h1>
              </div>
            ))}
          </div>
          <div className="flex justify-evenly pt-10 pb-16 md:pb-16">
            {teamMembers.map((member, index) => (
              <div
                key={index}
                onMouseEnter={() => setSelectedMember(member)}
              >
                <img
                  src={member.imgSrc}
                  alt=""
                  className="md:h-48 h-28 hover:shadow-2xl rounded-3xl hover:scale-110 transition-all duration-500"
                />
                <h1 className="pt-2 text-xl text-white font-bold">
                  {member.name.split(' ')[0]}
                </h1>
              </div>
            ))}
          </div>
        </div>
        <div className="md:w-1/2 text-center mt-36">
          <div className="relative">
            <div>
              <img
                src={mainImage}
                className="h-[650px] mx-auto pb-16 md:pb-0 transform scale-x-[-1]"
                alt=""
              />
            </div>
            <div className="absolute md:top-[-130px] top-[-190px]">
              <img
                src={callOut}
                alt=""
                className="md:h-96 h-72"
              />
              <div className="absolute md:top-20 top-10">
                <div className="w-10/12 mx-auto">
                  <h1 className="md:text-3xl text-xl">{selectedMember.name}</h1>
                  <h2 className="md:text-xl text-lg">
                    <span className="font-bold italic">aka</span> {selectedMember.title}
                  </h2>
                  <p className="md:text-[16px] text-[12px] pt-1">{selectedMember.description}</p>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Team;
